package chap01_basic;

public class less_8_Shape01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		*
		**
		***
		****
		*****
		*/
		for(int number = 1; number <= 5; number++)
		{
			for(int i = 1; i <= number;i++)
			{
				System.out.print("*");
			}
			System.out.println("");
		}
	}
	

}
